package com.company;public class Vector implements Comparable <Vector>{ public int[] x;public Vector(int[] x) {this.x = x;}public int[] getX(Vector vector){return x;}public static double getLength(Vector v) {int n = v.x.length;int i;double sum = 0;for (i = 0; i < n; i++) sum = sum + v.x[i] * v.x[i];sum = Math.sqrt(sum);return sum;}@Override public int compareTo(Vector o) {if(getLength(o) > getLength(new Vector(x))){return 1;}else if(getLength(o) < getLength(new Vector(x))){return -1;}else return 0;}public static void printVector(Vector vector) {int n = vector.getX(vector).length;System.out.print("[");for(int k = 0; k < n; k++) {if(k!= n-1){ System.out.print(vector.getX(vector)[k] + " ");}else{System.out.print(vector.getX(vector)[k]);}}System.out.print("] ");System.out.println(getLength(vector));}}
